import { Container, radioClasses } from "@mui/material";
import React, { useEffect, useState } from "react";
import DirectionsCarIcon from "@mui/icons-material/DirectionsCar";
import {
  useJsApiLoader,
  DirectionsService,
  GoogleMap,
  Marker,
  Polyline,
  DirectionsRenderer,
  InfoBox,
  InfoWindow,
} from "@react-google-maps/api";

const TrackOrder = ({ setroute,setdata,setorderdelivered}) => {
  const origin = { lat: 6.5244, lng: 3.3792 };
 
  const [Path, setpath] = useState(null);
  const destination = { lat: 6.4667, lng: 3.45 };
  const [dire, setd] = useState(null);
  const [post, setpos] = useState(null);
  const [index, setindex] = useState(40);
  const [open, isopen] = useState(false);
  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: "AIzaSyAm8wWzqS9Rltn5WvhUGqGZPeJsmJkykNU",
  });

  if (!isLoaded) {
    return <></>;
  }

  const Move = (g) => {
    var x = [];
    for (var i = 0; i < g.length; i++) {
      x.push([g[i].lat(), g[i].lng()]);
    }
    setpath(x);
    var y = 0;
    const z = setInterval(() => {
      if (y >= x.length) {
        setorderdelivered(true)
        clearInterval(z);
        return;
      } else {
        setpos(x[y]);

        y = y + 1;
      }
    }, 1000);
  };

  const get = () => {
    const directionsService = new window.google.maps.DirectionsService();
    directionsService.route(
      {
        origin: origin,
        destination: destination,
        travelMode: window.google.maps.TravelMode.DRIVING,
      },
      (result, status) => {
        if (status === window.google.maps.DirectionsStatus.OK) {
          setd(result);
          setdata({distance:result.routes[0].legs[0].distance.text,time:result.routes[0].legs[0].duration.text,from:origin,to:destination})
          var z = result.routes[0].legs[0].steps;

          if (z.length > 5) {
            var d = [z[0], z[1], z[2], z[z.length - 2], z[z.length - 1]];
            setroute(d);
          } else {
            setroute(z);
          }
          Move(result.routes[0].overview_path);
        } else {
          console.error(`error fetching directions ${result}`);
        }
      }
    );
  };
  return (
    <div style={{width:"100%",display:"flex",flexDirection:"row",justifyContent:"center"}}>
      <GoogleMap
        onLoad={() => {
          get();
          
        }}
        defaultCenter={{ lat: 6.5244, lng: 3.3792 }}
        zoom={10}
        mapContainerStyle={{ width: "95%", height: "30vh" }}
      >
        {post !== null && (
          <Marker
            onClick={(e) => isopen(true)}
            position={{ lat: post[0], lng: post[1] }}
          >
            {open === true && (
              <InfoWindow>
                <div>
                  <img
                    width={50}
                    height={50}
                    src="data:image/jpeg;base64,"
                  ></img>
                </div>
              </InfoWindow>
            )}
          </Marker>
        )}

        {dire != null && (
          <DirectionsRenderer directions={dire}></DirectionsRenderer>
        )}
      </GoogleMap>
    </div>
  );
};

export default TrackOrder;
